import CartActionTypes from './cart.types'

const INITIAL_STATE ={
    hidden : true,
    cartItems: []
};

const cartReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
      case CartActionTypes.TOGGLE_CART_HIDDEN:
        return {
          ...state,
          hidden: !state.hidden
        };
        case CartActionTypes.ADD_ITEM:
          return {
            ...state,
            cartItems: [...state.cartItems,  action.payload] //state.cartItems afisam toaate itemurile anterioare, action.payload adaugam la final celelate itemuri 
          }
        default:
      return state;
  }
};

export default cartReducer;
