import React from 'react';
import { Route } from 'react-router-dom';
import {connect} from 'react-redux';
import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import {createStructuredSelector } from 'reselect';
import { fetchCollectionsStartAsync  } from '../../redux/shop/shop.action';
import {selectIsCollectionFetching, selectIsCollectionLoading} from '../../redux/shop/shop.selector';

import WithSpinner from '../../components/with-spinner/with-spinner.component';
//import CollectionPreview from '../../components/collection-preview/colection-preview.component';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);
class ShopPage extends  React.Component{


componentDidMount(){
const {fetchCollectionsStartAsync} = this.props;

  fetchCollectionsStartAsync();
}

render(){
  const { match , isCollectionFetching, isCollectionLoaded} = this.props;
 
  
  return(

        <div className='shop-page'>
          <Route exact path={`${match.path}`} render ={(props)  => <CollectionsOverviewWithSpinner isLoading ={isCollectionFetching} {...props}/>} />
          <Route path={`${match.path}/:collectionId`} render ={(props)  => <CollectionPageWithSpinner isLoading ={!isCollectionLoaded} {...props}/>} />
        </div>
    
  )
}
} 
const mapStateToProps =createStructuredSelector({
  isCollectionFetching  : selectIsCollectionFetching,
  isCollectionLoaded  : selectIsCollectionLoading
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync : ()=> dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
