import {takeLatest, put, all, call} from 'redux-saga/effects';
import UserActionType from './user.types';
import {auth, googleProvider , dateUtilizator} from '../../firebase/firebase.utils'
import {googleSignInSucces, googleSignInFailure, emailSignInSucces, emailSignInFailure} from './user.actions';
export function* signInWithGoogle(){
    try{
        const {user} = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(dateUtilizator, user);
        const userSnapshot = yield userRef.get();
        yield put(googleSignInSucces({ id: userSnapshot.id, ...userSnapshot.data() }));
    } catch(error){
        yield put(googleSignInFailure(error));
    }
}
export function* onGoogleSignInStart() {
    yield takeLatest(UserActionType.GOOGLE_SIGN_IN_START, signInWithGoogle)
}
export function* signInWithEmail({payload: {email, password}}){
    try{
        const {user} = yield auth.signInWithEmailAndPassword(email, password);
        try{
            const {user} = yield auth.signInWithPopup(googleProvider);
            const userRef = yield call(dateUtilizator, user);
            const userSnapshot = yield userRef.get();
            yield put(emailSignInSucces({ id: userSnapshot.id, ...userSnapshot.data() }));
        } catch(error){
            yield put(googleSignInFailure(error));
        }
        yield put()
    } catch(error){
    yield put(emailSignInFailure(error))
    }
}
export function* onEmailSignInStart(){
    yield takeLatest(UserActionType.EMAIL_SIGN_IN_START, signInWithEmail)
}
export function* userSagas(){
    yield all([call(onGoogleSignInStart)])
}
